mapper.select:
select 

from lm_device_location_gps
where status=1
	and handle_flag = #{handleFlag,jdbcType=INTEGER}
	and time between #{fromTime,jdbcType=BIGINT} and #{toTime,jdbcType=BIGINT}
order by time asc

select 

from lm_device_location_gps
where status=1
	and time between #{fromTime,jdbcType=BIGINT} and #{toTime,jdbcType=BIGINT}
	and imei=#{imei,jdbcType=VARCHAR}
order by time asc
